actions.find.show_search_instructions_link = "Visualizza Le istruzione di Ricerca"
actions.find.advanced_search_heading = "Ricerca Avanzata"
actions.find.short_help_blurb = "Inserisci la tua ricerca nei campi seguenti e poi clicca su "XATAFACEQ"Cerca"XATAFACEQ""
actions.find.instructions.simple_searches.heading = "Ricerca Semplice"
actions.find.instructions.simple_searches_body = "Inserisci i valori nei campi corrispondenti e clicca sul tasto "XATAFACEQ"Cerca"XATAFACEQ" per trovare i record corrispondenti. 
                                        <br/>. Se tu inserisci "XATAFACEQ"Ane"XATAFACEQ" in un campo, la ricerca trova i record che contengono la frase "XATAFACEQ"Ane"XATAFACEQ". Questo include anche "XATAFACEQ"Cane"XATAFACEQ", "XATAFACEQ"Pane"XATAFACEQ""
actions.find.instructions.boolean_searches.body = "Se tu inserisci più citeri nei campi, verranno estratti i record per cui la ricerca valida TUTTI i campi."
actions.find.instructions.boolean_searches.heading = "Ricerca Booleana"
actions.find.instructions.exact_matches.heading = "Ricerca Esatta"
actions.find.instructions.exact_matches.body = "Preponi il segno ("XATAFACEQ"="XATAFACEQ") a ogni criterio per forzare la ricerca esatta. <br/>
Ad esempio se cerchi "XATAFACEQ"=ane"XATAFACEQ" troverà tutti i record che sono esattamente "XATAFACEQ"ane"XATAFACEQ" e non trova "XATAFACEQ"cane"XATAFACEQ" o "XATAFACEQ"pane"XATAFACEQ""
actions.find.instructions.less_than_greater_than.heading = "Ricerca Minore/Maggiore di"
actions.find.instructions.less_than_greater_than.body = "Preponendo il segno minore di ("XATAFACEQ"<"XATAFACEQ") o maggiore ("XATAFACEQ">"XATAFACEQ") si cercheranno i record che hanno un valore MINORE o MAGGIORE.
<br/> Ad esempio: Prezzo "XATAFACEQ">2000"XATAFACEQ" cerca tutti i record che hanno un Prezzo > 2000"
actions.find.instructions.range_searches.heading = "RIcerca per Insiemi"
actions.find.instructions.range_searches.body = "Per trovare i record in un insieme è possibile utilizzare i segni "XATAFACEQ"<"XATAFACEQ" e "XATAFACEQ">"XATAFACEQ" in modo combinato. <br/>
Ad esempio: Impostando nel campo "XATAFACEQ"prezzo"XATAFACEQ" i limiti "XATAFACEQ">20"XATAFACEQ" e "XATAFACEQ">200"XATAFACEQ" si trovano tutti i prezzi compresi tra 20 e 200."
actions.find.instructions.wildcard_searches.body = "
                                        <p>Per eseguire ricerche con caratteri jolly, anteporre una tilde ("XATAFACEQ"~"XATAFACEQ"), quindi includere uno dei caratteri jolly supportati nella query.</p>
                                        <p><b>Caratteri jolly supportati </b>:</p>
                                        <ul>
                                                <li><b>%</b> : Corrisponde a qualsiasi stringa di lunghezza 0 o più caratteri.</li>
                                                <li><b>_</b> : Corrisponde a un singolo carattere.</li>
                                        </ul>
                                        
                                        <p>Esempi:</p>
                                        <ul>
                                                <li>"XATAFACEQ"~J%y"XATAFACEQ" verranno cercati tutti i valori che del campo che inizia con  "XATAFACEQ"J"XATAFACEQ" e finisce con "XATAFACEQ"y"XATAFACEQ".  Per esempio, troverà sia "XATAFACEQ"January"XATAFACEQ" che "XATAFACEQ"Joy"XATAFACEQ", ma non "XATAFACEQ"Joyous"XATAFACEQ" o "XATAFACEQ"AJoy"XATAFACEQ"</li>
                                                <li>"XATAFACEQ"~B%"XATAFACEQ" verranno cercati tutti i valori del campo che iniziano con "XATAFACEQ"B"XATAFACEQ"</li>
                                                <li>"XATAFACEQ"~B_b"XATAFACEQ" erranno cercati tutti i valori del campo che iniziano e finiscono con "XATAFACEQ"b"XATAFACEQ" e che sono lunghi 3 caratteri.  Questo troverà "XATAFACEQ"bib"XATAFACEQ" e "XATAFACEQ"bob"XATAFACEQ" ma non "XATAFACEQ"boob"XATAFACEQ"</li>
                                        </ul>"
